package QuickSort;import java.util.Arrays;/** * @author: jt * @date: 2018-10-10 */public class QuickSort2 {    public static void main(String[] args) {        int[] unsorted={6,5,3,1,8,7,2,4};        System.out.println(Arrays.toString(unsorted));        QuickSortTest algorithm = new QuickSortTest();        algorithm.sort(unsorted);        System.out.println(Arrays.toString(unsorted));    }}class QuickSortTest{    private int[] input;    private int length;    public void sort(int[] numbers){        if(numbers==null || numbers.length==0){            return;        }        this.input=numbers;        length=numbers.length;        quickSort(0, length -1);    }    public void quickSort(int low, int high) {        int i=low;        int j=high;        int pivot =input[low + (high-low)/2];        while(i<=j){            while(input[i]<pivot){                i++;            }            while (input[j]>pivot){                j--;            }            if(i<=j){                swap(i,j);                i++;                j--;            }        }        if(low<j){            quickSort(low,j);        }        if(i<high){            quickSort(i,high);        }    }    private void swap(int i, int j) {        int temp=input[i];        input[i]=input[j];        input[j]=temp;    }}