package leetcode;/** * @author: jt * @date: 2018-10-11 *//** * 模仿二进制相加 */public class AddBinary {    public static void main(String[] args) {        String a ="1000";        String b="1000";        String add = Add(a, b);        System.out.println(add);    }    public static String Add(String a,String b){        int i=a.length()-1,j=b.length()-1,carry=0;        StringBuilder stringBuilder = new StringBuilder();        while (carry==1 || i>=0 || j>=0){            if(i>=0 && a.charAt(i--)=='1'){                carry++;            }            if(j>=0 && b.charAt(j--)=='1'){                carry++;            }            stringBuilder.append(carry%2);            carry/=2;        }        return stringBuilder.reverse().toString();    }}