package threesum;/** * @author: jt * @date: 2018-10-4 */public class ThreeSumSlow implements ThreeSum {    public int count(int[] nums) {        int length = nums.length;        int cnt=0;        for (int i = 0; i < length; i++) {            for (int j = i+1; j < length; j++) {                for (int k = 0; k < length; k++) {                    if(nums[i]+nums[j]+nums[k]==0){                        cnt++;                    }                }            }        }        return cnt;    }}